'use client';

import { Image, Space, Tooltip } from 'antd';
import {
  DownloadOutlined,
  RotateLeftOutlined,
  RotateRightOutlined,
  SwapOutlined,
  ZoomInOutlined,
  ZoomOutOutlined,
} from '@ant-design/icons';
import { FC } from 'react';

const PreviewGroup = Image.PreviewGroup;

// TODO: replace with actual images from the server
const src = 'https://picsum.photos/500';
const images = Array.from({ length: 20 }, (_, i) => ({
  width: 200,
  height: 200,
  src:
    i == 0
      ? 'https://images.unsplash.com/photo-1712698396006-1996dc7cb2cc?q=80&w=2701&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'
      : `${src}?random=${i}`,
}));

const fallback =
  'data:image/png;base64,';

const Gallery: FC = () => {
  const onDownload = (imgIndex: number) => {
    const img = images[imgIndex];
    fetch(img.src)
      .then((response) => response.blob())
      .then((blob) => {
        const url = URL.createObjectURL(new Blob([blob]));
        const link = document.createElement('a');
        link.href = url;
        link.download = 'image.png';
        document.body.appendChild(link);
        link.click();
        URL.revokeObjectURL(url);
        link.remove();
      });
  };

  return (
    <PreviewGroup
      preview={{
        countRender: () => null,
        toolbarRender: (
          _,
          {
            transform: { scale },
            actions: {
              onFlipY,
              onFlipX,
              onRotateLeft,
              onRotateRight,
              onZoomOut,
              onZoomIn,
            },
            current,
          }
        ) => {
          // TODO: handle max zoom in and outs
          return (
            <Space size={30} className='toolbar-wrapper'>
              <DownloadOutlined
                onClick={() => onDownload(current)}
                className='toolbar-icon'
              />
              <SwapOutlined
                rotate={90}
                onClick={onFlipY}
                className='toolbar-icon'
              />
              <SwapOutlined onClick={onFlipX} className='toolbar-icon' />
              <RotateLeftOutlined
                onClick={onRotateLeft}
                className='toolbar-icon'
              />
              <RotateRightOutlined
                onClick={onRotateRight}
                className='toolbar-icon'
              />
              <ZoomOutOutlined
                disabled={scale === 1}
                onClick={onZoomOut}
                className='toolbar-icon'
              />
              <ZoomInOutlined
                disabled={scale === 50}
                onClick={onZoomIn}
                className='toolbar-icon'
              />
              <Tooltip title='Preview your Avatar!'>
                <a href='/?id=123456' target='_blank' className='toolbar-icon'>
                  Preview
                </a>
              </Tooltip>
            </Space>
          );
        },
      }}
    >
      {images.map(({ width, height, src }, i) => (
        <Image
          key={i}
          width={width}
          height={height}
          src={src}
          fallback={fallback}
          className='p-0.5'
        />
      ))}
    </PreviewGroup>
  );
};

export default Gallery;
